import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { authAPI } from '../../services/api';
import SessionConflictModal from '../SessionConflictModal';
import toast from 'react-hot-toast';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showSessionConflict, setShowSessionConflict] = useState(false);
  const [existingSession, setExistingSession] = useState(null);
  const [forceLoginLoading, setForceLoginLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Use authAPI directly for session conflict handling
      const response = await authAPI.login(formData);
      console.log('沐 Login Response:', response);
      
      // Check for session conflict
      if (response.status === 409 && response.conflict) {
        setExistingSession(response.existingSession);
        setShowSessionConflict(true);
        setLoading(false);
        return;
      }
      
      if (response.success && response.data) {
        const { data } = response;
        console.log('沐 Login Data:', data);
        
        // Use AuthContext login to update state
        const result = await login(formData);
        
        if (result.success) {
          toast.success('ﾄ斉ハg nh蘯ｭp thﾃnh cﾃｴng!');
          
          // Navigate based on user role
          const userRole = data.user.role;
          if (userRole === 'customer') {
            navigate('/feedback');
          } else {
            navigate('/');
          }
        }
      } else {
        console.log('沐 Login failed:', response);
        setError(response.message || 'ﾄ斉ハg nh蘯ｭp th蘯･t b蘯｡i');
        toast.error(response.message || 'ﾄ斉ハg nh蘯ｭp th蘯･t b蘯｡i');
      }
    } catch (error) {
      console.error('Login error:', error);
      
      // Check for session conflict in error response
      if (error.response?.status === 409 && error.response?.data?.conflict) {
        setExistingSession(error.response.data.existingSession);
        setShowSessionConflict(true);
        setLoading(false);
        return;
      }
      
      setError(error.response?.data?.message || 'Cﾃｳ l盻擁 x蘯｣y ra khi ﾄ惰ハg nh蘯ｭp');
      toast.error(error.response?.data?.message || 'Cﾃｳ l盻擁 x蘯｣y ra khi ﾄ惰ハg nh蘯ｭp');
    } finally {
      setLoading(false);
    }
  };

  const handleForceLogin = async () => {
    setForceLoginLoading(true);
    setError('');

    try {
      const response = await authAPI.forceLogin(formData);
      
      if (response.success && response.data) {
        const { data } = response;
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        
        toast.success('ﾄ斉ハg nh蘯ｭp thﾃnh cﾃｴng. Phiﾃｪn cﾅｩ ﾄ妥｣ ﾄ柁ｰ盻｣c ﾄ惰ハg xu蘯･t.');
        setShowSessionConflict(false);
        
        // Force page reload to update authentication state
        window.location.href = data.user.role === 'customer' ? '/feedback' : '/dashboard';
      } else {
        setError(response.message || 'Force login th蘯･t b蘯｡i');
      }
    } catch (error) {
      console.error('Force login error:', error);
      setError(error.response?.data?.message || 'Force login th蘯･t b蘯｡i');
    } finally {
      setForceLoginLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            ﾄ斉ハg nh蘯ｭp tﾃi kho蘯｣n
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Ho蘯ｷc{' '}
            <Link
              to="/register"
              className="font-medium text-indigo-600 hover:text-indigo-500"
            >
              t蘯｡o tﾃi kho蘯｣n m盻嬖
            </Link>
          </p>
        </div>
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
              {error}
            </div>
          )}
          
          <div className="space-y-4">
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email
              </label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="mt-1 appearance-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                placeholder="Nh蘯ｭp email c盻ｧa b蘯｡n"
              />
            </div>
            
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                M蘯ｭt kh蘯ｩu
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete="current-password"
                required
                value={formData.password}
                onChange={handleChange}
                className="mt-1 appearance-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                placeholder="Nh蘯ｭp m蘯ｭt kh蘯ｩu"
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? 'ﾄ紳ng ﾄ惰ハg nh蘯ｭp...' : 'ﾄ斉ハg nh蘯ｭp'}
            </button>
          </div>

          <div className="text-center">
            <Link
              to="/create-super-admin"
              className="text-sm text-indigo-600 hover:text-indigo-500"
            >
              T蘯｡o tﾃi kho蘯｣n Super Admin
            </Link>
          </div>
        </form>
      </div>

      {/* Session Conflict Modal */}
      <SessionConflictModal
        isOpen={showSessionConflict}
        onClose={() => setShowSessionConflict(false)}
        onForceLogin={handleForceLogin}
        existingSession={existingSession}
        loading={forceLoginLoading}
      />
    </div>
  );
};

export default Login;
